-- ############## 20260223_translations_story_subtypes.sql ##############
-- ============================================================
-- T-C1: DB-Übersetzungen — story_subtypes
-- Adds: tr, bg, ro, pl, lt, hu, ca, sl to labels + descriptions
-- Merge via JSONB ||. WHERE theme_key + subtype_key.
-- ============================================================

UPDATE story_subtypes SET labels = labels || '{"tr": "Büyülü Orman", "bg": "Магическа гора", "ro": "Pădure încântată", "pl": "Zaczarowany las", "lt": "Apburta miška", "hu": "Varázserdő", "ca": "Bosc encantat", "sl": "Začarani gozd"}'::jsonb, descriptions = descriptions || '{"tr": "Konuşan hayvanlar ve gizemli yaratıklarla dolu büyülü bir ormanda geçen bir hikaye", "bg": "История в магическа гора с говорещи животни и мистериозни създания", "ro": "O poveste într-o pădure magică plină de animale vorbitoare și ființe misterioase", "pl": "Historia w magicznym lesie pełnym mówiących zwierząt i tajemniczych stworzeń", "lt": "Pasaka magiškame miške, pilname kalbančių gyvūnų ir paslaptingų būtybių", "hu": "Egy történet egy varázserdőben, tele beszélő állatokkal és rejtélyes lényekkel", "ca": "Una història en un bosc màgic ple d''animals parlants i criatures misterioses", "sl": "Zgodba v čarobnem gozdu polnem govorečih živali in skrivnostnih bitij"}'::jsonb WHERE theme_key = 'magic_fantasy' AND subtype_key = 'enchanted_forest';

UPDATE story_subtypes SET labels = labels || '{"tr": "Masal Farklı", "bg": "Приказка с обрат", "ro": "Basm cu twist", "pl": "Baśń na opak", "lt": "Pasaka kitaip", "hu": "Tündérmese megfordítva", "ca": "Conte de fades amb gir", "sl": "Pravljica na novo"}'::jsonb, descriptions = descriptions || '{"tr": "Tanıdık bir masal motifi yeni ve şaşırtıcı şekilde anlatılır", "bg": "Познат приказен мотив разказан по нов и изненадващ начин", "ro": "Un motiv de basm cunoscut repovestit într-un mod nou și surprinzător", "pl": "Znany motyw baśniowy opowiedziany w nowy i zaskakujący sposób", "lt": "Pažįstama pasakos tema pasakojama naujai ir stebinančiai", "hu": "Egy ismert tündérmese motívum új és meglepő módon mesélve", "ca": "Un motiu de conte conegut explicat d''una manera nova i sorprenent", "sl": "Znana pravljična motivacija pripovedana na nov in presenetljiv način"}'::jsonb WHERE theme_key = 'magic_fantasy' AND subtype_key = 'fairy_tale_twist';

UPDATE story_subtypes SET labels = labels || '{"tr": "Büyülü Yaratık Dostluğu", "bg": "Приятелство с магическо създание", "ro": "Prietenie cu o creatură magică", "pl": "Przyjaźń z magicznym stworzeniem", "lt": "Draugystė su magiška būtybe", "hu": "Varázslény barátság", "ca": "Amistat amb criatura màgica", "sl": "Prijateljstvo z magičnim bitjem"}'::jsonb, descriptions = descriptions || '{"tr": "Bir çocuk büyülü bir varlık bulur veya kurtarır ve ayrılmaz arkadaş olurlar", "bg": "Детето намира или спасява магическо създание и стават неразделни приятели", "ro": "Un copil găsește sau salvează o creatură magică și devin prieteni inseparabili", "pl": "Dziecko znajduje lub ratuje magiczne stworzenie i stają się nierozłączni", "lt": "Vaikas randa arba gelbėja magišką būtybę ir jie tampa neatskiriamais draugais", "hu": "Egy gyerek talál vagy megment egy varázslényt, és elválaszthatatlan barátok lesznek", "ca": "Un nen troba o rescata una criatura màgica i es fan amics inseparables", "sl": "Otrok najde ali reši čarobno bitje in postaneta neločljiva prijatelja"}'::jsonb WHERE theme_key = 'magic_fantasy' AND subtype_key = 'magical_creature_friendship';

UPDATE story_subtypes SET labels = labels || '{"tr": "Küçülme ve Büyüme", "bg": "Свиване и растеж", "ro": "Mic și mare", "pl": "Kurczenie i rośnięcie", "lt": "Mažėjimas ir augimas", "hu": "Zsugorodás és növekedés", "ca": "Enconguir-se i créixer", "sl": "Skrčenje in rast"}'::jsonb, descriptions = descriptions || '{"tr": "Biri karınca boyuna küçülür veya evlerin üzerine çıkacak kadar büyür", "bg": "Някой се свива до размера на мравка или пораства над къщите", "ro": "Cineva se micșorează la mărimea unei furnici sau crește mai mare decât casele", "pl": "Ktoś kurczy się do rozmiaru mrówki lub rośnie wyżej niż domy", "lt": "Kažkas susitraukia iki skruzdėlos dydžio arba išauga aukščiau nei namai", "hu": "Valaki hangyaméretre zsugorodik vagy magasabbra nő a házaknál", "ca": "Algú s''encongeix com una formiga o creix més alt que les cases", "sl": "Nekdo se skrči na velikost mravlje ali zraste višje od hiš"}'::jsonb WHERE theme_key = 'magic_fantasy' AND subtype_key = 'shrinking_growing';

UPDATE story_subtypes SET labels = labels || '{"tr": "Uçuş Macerası", "bg": "Летящо приключение", "ro": "Aventură zburătoare", "pl": "Latająca przygoda", "lt": "Skraidymo nuotykis", "hu": "Repülő kaland", "ca": "Aventura voladora", "sl": "Leteča pustolovščina"}'::jsonb, descriptions = descriptions || '{"tr": "Büyülü bir nesne, yaratık veya kendi gücünle uçmak", "bg": "Летене с магически предмет, създание или със собствени сили", "ro": "Zburat pe un obiect magic, cu o creatură sau cu puteri proprii", "pl": "Latanie na magicznym przedmiocie, stworze lub własną mocą", "lt": "Skraidymas ant magiško daikto, būtybės ar savo jėga", "hu": "Repülés varázstárgyon, lényen vagy saját erőből", "ca": "Volar amb un objecte màgic, una criatura o amb els teus poders", "sl": "Letenje na čarobnem predmetu, bitju ali z lastno močjo"}'::jsonb WHERE theme_key = 'magic_fantasy' AND subtype_key = 'flying_adventure';

UPDATE story_subtypes SET labels = labels || '{"tr": "Başka Dünyaya Portal", "bg": "Портал към друг свят", "ro": "Portal către altă lume", "pl": "Portal do innego świata", "lt": "Portalas į kitą pasaulį", "hu": "Portál másik világba", "ca": "Portal a un altre món", "sl": "Portal v drug svet"}'::jsonb, descriptions = descriptions || '{"tr": "Büyülü bir kapı, kitap veya aynadan tamamen farklı bir dünyaya yolculuk", "bg": "Пътуване през магическа врата, книга или огледало в съвсем друг свят", "ro": "Călătorie printr-o ușă magică, carte sau oglindă într-o lume complet diferită", "pl": "Podróż przez magiczne drzwi, książkę lub lustro do zupełnie innego świata", "lt": "Kelionė per magiškas duris, knygą ar veidrodį į visiškai kitokią pasaulį", "hu": "Utazás varázsajtón, könyvön vagy tükörön keresztül egy teljesen más világba", "ca": "Viatge a través d''una porta màgica, llibre o mirall a un món completament diferent", "sl": "Potovanje skozi čarobna vrata, knjigo ali ogledalo v popolnoma drug svet"}'::jsonb WHERE theme_key = 'magic_fantasy' AND subtype_key = 'portal_other_world';

UPDATE story_subtypes SET labels = labels || '{"tr": "Büyücü Okulu", "bg": "Училище за магия", "ro": "Școala de vrăjitori", "pl": "Szkoła czarodziejów", "lt": "Burtininkų mokykla", "hu": "Varázslóiskola", "ca": "Escola de bruixots", "sl": "Čarovniška šola"}'::jsonb, descriptions = descriptions || '{"tr": "Büyü, iksir ve sihirli dersler öğrenilen bir okulda maceralar", "bg": "Приключения в училище, където се учат заклинания, отвари и магически предмети", "ro": "Aventuri la o școală unde înveți vrăji, poțiuni și materii magice", "pl": "Przygody w szkole, gdzie uczy się zaklęć, eliksirów i magii", "lt": "Nuotykiai mokykloje, kur mokomasi burtų, eliksyrų ir magiškų dalykų", "hu": "Kalandok egy iskolában, ahol varázslatokat, bájitalokat és mágiát tanulnak", "ca": "Aventures en una escola on s''aprenen encanteris, pócimes i matèries màgiques", "sl": "Pustolovščine v šoli, kjer se učijo uroke, napitnice in čarobne predmete"}'::jsonb WHERE theme_key = 'magic_fantasy' AND subtype_key = 'wizard_school';

UPDATE story_subtypes SET labels = labels || '{"tr": "Laneti Kırmak", "bg": "Премахване на проклятието", "ro": "Ruperea blestemului", "pl": "Przełamanie klątwy", "lt": "Kerų laužymas", "hu": "Átok megtörése", "ca": "Trencar la maledicció", "sl": "Prekletstvo prekiniti"}'::jsonb, descriptions = descriptions || '{"tr": "Bir lanet kırılmalı — ama çözüm beklediğin gibi değil", "bg": "Проклятието трябва да бъде премахнато — но решението не е това, което очакваш", "ro": "Un blestem trebuie rupt — dar soluția nu e ce te aștepți", "pl": "Klątwa musi zostać przełamana — ale rozwiązanie jest inne niż myślisz", "lt": "Kerai turi būti laužomi — bet sprendimas ne toks, kokio tikėjaisi", "hu": "Az átoknak törnie kell — de a megoldás nem az, amire számítasz", "ca": "Una maledicció s''ha de trencar — però la solució no és la que t''esperes", "sl": "Prekletstvo je treba prekiniti — a rešitev ni tista, ki jo pričakuješ"}'::jsonb WHERE theme_key = 'magic_fantasy' AND subtype_key = 'curse_breaking';

UPDATE story_subtypes SET labels = labels || '{"tr": "Rüya Yolculuğu", "bg": "Пътуване в съня", "ro": "Călătorie în vis", "pl": "Podróż we śnie", "lt": "Sapno kelionė", "hu": "Álomutazás", "ca": "Viatge de somnis", "sl": "Potovanje v sanjah"}'::jsonb, descriptions = descriptions || '{"tr": "Rüyada, rüya görenin duygularına göre değişen fantastik bir dünyada yolculuk", "bg": "Пътуване в фантастичен свят на сънища, който се променя с емоциите на сънуващия", "ro": "Călătorie printr-o lume de vis fantastică care se schimbă cu emoțiile visătorului", "pl": "Podróż przez fantastyczny świat snów zmieniający się z emocjami śniącego", "lt": "Kelionė fantastiška sapnų pasauliu, kuris kinta su sapnuotojo emocijomis", "hu": "Utazás egy fantasztikus álomvilágban, ami az álmodó érzelmeihez igazodik", "ca": "Viatge per un món de somnis fantàstic que canvia amb les emocions del somiador", "sl": "Potovanje skozi fantastičen svet sanj, ki se spreminja z čustvi sanjača"}'::jsonb WHERE theme_key = 'magic_fantasy' AND subtype_key = 'dream_journey';

UPDATE story_subtypes SET labels = labels || '{"tr": "Sualtı Krallığı", "bg": "Подводно кралство", "ro": "Regatul subacvatic", "pl": "Podwodne królestwo", "lt": "Povandeninė karalystė", "hu": "Vízalatti királyság", "ca": "Regne submarí", "sl": "Podvodno kraljestvo"}'::jsonb, descriptions = descriptions || '{"tr": "Sualtı maceralar: deniz kızları, batık şehirler, konuşan balıklar", "bg": "Подводни приключения: русалки, потънали градове, говорещи риби", "ro": "Aventuri subacvatice: sirene, orașe scufundate, pești vorbitori", "pl": "Podwodne przygody: syreny, zatopione miasta, mówiące ryby", "lt": "Povandeninės nuotykiai: undinės, nuskendusios miestai, kalbančios žuvys", "hu": "Vízalatti kalandok: sellők, elsüllyedt városok, beszélő halak", "ca": "Aventures submarines: sirenes, ciutats submergides, peixos parlants", "sl": "Podvodne pustolovščine: sirene, potopljena mesta, govoreče ribe"}'::jsonb WHERE theme_key = 'magic_fantasy' AND subtype_key = 'underwater_kingdom';

UPDATE story_subtypes SET labels = labels || '{"tr": "Zaman Döngüsü", "bg": "Времева примка", "ro": "Buclă temporală", "pl": "Pętla czasu", "lt": "Laiko kilpa", "hu": "Időhurok", "ca": "Bucle temporal", "sl": "Časovna zanka"}'::jsonb, descriptions = descriptions || '{"tr": "Aynı günü tekrar tekrar yaşamak ve nedenini bulmak", "bg": "Да преживееш същия ден отново и отново и да разбереш защо", "ro": "Să trăiești aceeași zi iar și iar și să înțelegi de ce", "pl": "Przeżywanie tego samego dnia w kółko i odkrywanie dlaczego", "lt": "Gyventi tą pačią dieną vėl ir vėl bei išsiaiškinti kodėl", "hu": "Ugyanazt a napot újra és újra élni és kideríteni miért", "ca": "Viure el mateix dia una i altra vegada i esbrinar per què", "sl": "Živeti isti dan znova in znova in ugotoviti zakaj"}'::jsonb WHERE theme_key = 'magic_fantasy' AND subtype_key = 'time_loop';

UPDATE story_subtypes SET labels = labels || '{"tr": "Görünmez Dünya", "bg": "Невидимият свят", "ro": "Lumea invizibilă", "pl": "Niewidzialny świat", "lt": "Nematomas pasaulis", "hu": "A láthatatlan világ", "ca": "El món invisible", "sl": "Nevidni svet"}'::jsonb, descriptions = descriptions || '{"tr": "Bizimkine paralel gizli bir dünya var — onu sadece birkaç kişi görebilir", "bg": "Скрит свят съществува успоредно на нашия — само малцина могат да го видят", "ro": "O lume ascunsă există paralel cu a noastră — doar câțiva o pot vedea", "pl": "Ukryty świat istnieje równolegle do naszego — tylko nieliczni go widzą", "lt": "Slaptas pasaulis egzistuoja lygiagrečiai su mūsų — tik keli gali jį matyti", "hu": "Egy rejtett világ párhuzamosan létezik a mienkkel — csak kevesen látják", "ca": "Un món amagat existeix en paral·lel al nostre — només uns pocs el poden veure", "sl": "Skrit svet obstaja vzporedno z našim — le redki ga vidijo"}'::jsonb WHERE theme_key = 'magic_fantasy' AND subtype_key = 'invisible_world';

